/**
* @brief  Read a received packet.
* @param  heth: pointer to a ETH_HandleTypeDef structure that contains
*         the configuration information for ETHERNET module
* @param  pAppBuff: Pointer to an application buffer to receive the packet.
* @retval HAL status
*/
HAL_StatusTypeDef HAL_ETH_ReadData(ETH_HandleTypeDef *heth, void **pAppBuff)
{
  static uint8_t rx_buffer[ETH_MAX_PACKET_SIZE];
  int received_length;

  if (pAppBuff == NULL)
  {
    heth->ErrorCode |= HAL_ETH_ERROR_PARAM;
    return HAL_ERROR;
  }

  if (heth->gState != HAL_ETH_STATE_STARTED)
  {
    return HAL_ERROR;
  }

  /* Use backend Ethernet interface to receive packet */
  received_length = HAL_BE_ENET_ReadFrame(rx_buffer, ETH_MAX_PACKET_SIZE);

  if (received_length > 0)
  {
    /* Simulate successful packet reception */
    *pAppBuff = rx_buffer;

    /* Update receive statistics */
    heth->RxDescList.RxDataLength = received_length;

    return HAL_OK;
  }

  /* No packet available */
  return HAL_ERROR;
}
