    /* Incr current tx desc index */
    INCR_TX_DESC_INDEX(heth->TxDescList.CurTxDesc, 1U);

    /* POSIX replacement: Simulate transmission completion immediately */
    /* In POSIX environment, we assume transmission is always successful */

    tickstart = HAL_GetTick();

