/**
* @brief  Initializes the DMA Rx descriptors in chain mode.
*         called by HAL_ETH_Init() API.
* @param  heth: pointer to a ETH_HandleTypeDef structure that contains
*         the configuration information for ETHERNET module
* @retval None
*/
static void ETH_DMARxDescListInit(ETH_HandleTypeDef *heth)
{
  ETH_DMADescTypeDef *dmarxdesc;
  uint32_t i;

  for (i = 0; i < (uint32_t)ETH_RX_DESC_CNT; i++)
  {
    dmarxdesc =  heth->Init.RxDesc + i;

    /* Initialize descriptor fields */
    dmarxdesc->DESC0 = 0x0U;
    dmarxdesc->DESC1 = 0x0U;
    dmarxdesc->DESC2 = 0x0U;
    dmarxdesc->DESC3 = 0x0U;
    dmarxdesc->BackupAddr0 = 0x0U;
    dmarxdesc->BackupAddr1 = 0x0U;

    /* Set Own bit of the Rx descriptor Status */
    dmarxdesc->DESC0 = ETH_DMARXDESC_OWN;

    /* Set Buffer1 size and Second Address Chained bit */
    dmarxdesc->DESC1 = heth->Init.RxBuffLen | ETH_DMARXDESC_RCH;

    /* Enable Ethernet DMA Rx Descriptor interrupt */
    dmarxdesc->DESC1 &= ~ETH_DMARXDESC_DIC;

    /* Set Rx descriptors addresses */
    heth->RxDescList.RxDesc[i] = (uint32_t)dmarxdesc;

    if (i < ((uint32_t)ETH_RX_DESC_CNT - 1U))
    {
      dmarxdesc->DESC3 = (uint32_t)(heth->Init.RxDesc + i + 1U);
    }
    else
    {
      dmarxdesc->DESC3 = (uint32_t)(heth->Init.RxDesc);
    }
  }

  /* Initialize descriptor list management variables */
  heth->RxDescList.RxDescIdx = 0U;
  heth->RxDescList.RxDescCnt = 0U;
  heth->RxDescList.RxBuildDescIdx = 0U;
  heth->RxDescList.RxBuildDescCnt = 0U;
  heth->RxDescList.ItMode = 0U;

  /* Note: Hardware register access (DMARDLAR) removed for POSIX compatibility */
}
