  for (i = 0; i < (uint32_t)ETH_TX_DESC_CNT; i++)
  {
    dmatxdesc = heth->Init.TxDesc + i;

    /* Initialize descriptor fields - remove WRITE_REG operations */
    dmatxdesc->DESC0 = 0x0U;
    dmatxdesc->DESC1 = 0x0U;
    dmatxdesc->DESC2 = 0x0U;
    dmatxdesc->DESC3 = 0x0U;

