    {
      dmatxdesc->DESC3 = (uint32_t)(heth->Init.TxDesc + i + 1U);
    }
    else
    {
      dmatxdesc->DESC3 = (uint32_t)(heth->Init.TxDesc);
    }

    /* Set the DMA Tx descriptors checksum insertion */
    dmatxdesc->DESC0 |= ETH_DMATXDESC_CHECKSUMTCPUDPICMPFULL;
  }

  heth->TxDescList.CurTxDesc = 0;

