/**
* @brief  Read command (DCS or generic) - POSIX Replacement
* @param  hdsi  pointer to a DSI_HandleTypeDef structure that contains
*               the configuration information for the DSI.
* @param  ChannelNbr  Virtual channel ID
* @param  Array pointer to a buffer to store the payload of a read back operation.
* @param  Size  Data size to be read (in byte).
* @param  Mode  DSI read packet data type.
*               This parameter can be any value of @arg DSI_SHORT_READ_PKT_Data_Type.
* @param  DCSCmd  DCS get/read command.
* @param  ParametersTable  Pointer to parameter values table.
* @retval HAL status
*/
HAL_StatusTypeDef HAL_DSI_Read(DSI_HandleTypeDef *hdsi,
                               uint32_t ChannelNbr,
                               uint8_t *Array,
                               uint32_t Size,
                               uint32_t Mode,
                               uint32_t DCSCmd,
                               uint8_t *ParametersTable)
{
  /* Process locked */
  __HAL_LOCK(hdsi);

  /* Check the parameters */
  assert_param(IS_DSI_READ_PACKET_TYPE(Mode));

  /* Use POSIX backend interface to read data */
  int bytes_read = HAL_BE_In(Array, Size);

  /* Process unlocked */
  __HAL_UNLOCK(hdsi);

  if (bytes_read == Size) {
    return HAL_OK;
  } else if (bytes_read < 0) {
    return HAL_ERROR;
  } else {
    return HAL_TIMEOUT;
  }
}
